mapper.select:
SELECT img_url
from house_img
where house_id = #{houseId}

SELECT house_id, name, price, position, area, des
FROM house where 1=1

    and price <= #{maxPrice} and price >= #{minPrice}


    and area <= #{maxArea} and area >= #{minArea}


    and region = #{region}


    and type = #{type}


    and "dec" = #{dec}


    and route = #{route}


    and station = #{station}
